  template: `
  <div class="list-item {{ task?.state }}">
  <label
    [attr.aria-label]="'archiveTask-' + task.id"
    for="checked-{{ task?.id }}"
    class="checkbox"
  >
    <input
      type="checkbox"
      disabled="true"
      [defaultChecked]="task?.state === 'TASK_ARCHIVED'"
      name="checked-{{ task?.id }}"
      id="checked-{{ task?.id }}"
    />
    <span class="checkbox-custom" (click)="onArchive(task.id)"></span>
  </label>
  <label
    [attr.aria-label]="task.title + ''"
    for="title-{{ task?.id }}"
    class="title"
  >
    <input
      type="text"
      [value]="task.title"
      readonly="true"
      id="title-{{ task?.id }}"
      name="title-{{ task?.id }}"
      placeholder="Input title"
    />
  </label>
  <button
    *ngIf="task?.state !== 'TASK_ARCHIVED'"
    class="pin-button"
    [attr.aria-label]="'pinTask-' + task.id"
    (click)="onPin(task.id)"
  >
    <span class="icon-star"></span>
  </button>
</div>
`,
})
export class TaskComponent {
  @Input() task: Task;

  // tslint:disable-next-line: no-output-on-prefix
  @Output()
  onPinTask = new EventEmitter<Event>();

  // tslint:disable-next-line: no-output-on-prefix
  @Output()
  onArchiveTask = new EventEmitter<Event>();
  /**
   * Component method to trigger the onPin event
   * @param id string
   */
   onPin(id: any) {
    this.onPinTask.emit(id);
  }
  /**
   * Component method to trigger the onArchive event
   * @param id string
   */
  onArchive(id: any) {
    this.onArchiveTask.emit(id);
  }
}

import { Component } from '@angular/core';
import { Store } from '@ngxs/store'; // read data from the store
import { ArchiveTask, PinTask } from '../state/task.state';
import { Observable } from 'rxjs';


// keep the presentational version of the TaskList separate is that it is easier to test and isolate.
// As it doesn't rely on the presence of a store, it is much easier to deal with from a testing perspective.
@Component({
  selector: 'app-task-list',
  template: `
    <app-pure-task-list
      [tasks]="tasks$ | async"
      (onArchiveTask)="archiveTask($event)"
      (onPinTask)="pinTask($event)"
    ></app-pure-task-list>
  `,
})
export class TaskListComponent {
  tasks$?: Observable<any>;

  constructor(private store: Store) {
     this.tasks$ = store.select((state) => state.taskbox.tasks);
  }

  /**
   * Component method to trigger the archiveTask event
   */
  archiveTask(id: string) {
    this.store.dispatch(new ArchiveTask(id));
  }

  /**
   * Component method to trigger the pinTask event
   */
  pinTask(id: string) {
    this.store.dispatch(new PinTask(id));
  }
}

